// src/pages/newmusic.tsx
import React, { useState } from "react";
import { Layout, styles } from "../layout/layout";
import roseMp4 from "../media/a-single-pedal-of-a-rose.mp4"; // <-- make sure this file exists

const NewMusic: React.FC = () => {
  const [videoError, setVideoError] = useState<string | null>(null);

  const handleVideoError: React.ReactEventHandler<HTMLVideoElement> = (e) => {
    console.error("Video failed to load/play:", e.currentTarget.currentSrc);
    setVideoError("The video could not be loaded.");
  };

  const handleLoadedMetadata: React.ReactEventHandler<HTMLVideoElement> = (e) => {
    const el = e.currentTarget;
    console.log("Video metadata loaded:", {
      duration: el.duration,
      videoWidth: el.videoWidth,
      videoHeight: el.videoHeight,
      currentSrc: el.currentSrc,
    });
    setVideoError(null);
  };

  return (
    <Layout title="New Music">
      <p style={styles.paragraph}>
        This page hosts embedded videos. Below is <em>A Single Pedal of a Rose</em>.
      </p>

      <div style={{ textAlign: "center", margin: "20px 0" }}>
        <video
          src={roseMp4}
          controls
          preload="metadata"
          playsInline
          onError={handleVideoError}
          onLoadedMetadata={handleLoadedMetadata}
          style={{
            width: "100%",
            maxWidth: 800,
            borderRadius: 8,
            outline: "none",
            backgroundColor: "#000",
          }}
          // Optional: add a poster image to src/media and import it just like the mp4
          // poster={posterImg}
        >
          Your browser does not support the video tag.
        </video>

        {videoError && (
          <div style={{ marginTop: 12, color: "#b00020" }}>
            <p style={{ margin: 0 }}>{videoError}</p>
            <p style={{ margin: "6px 0 0" }}>
              Try opening the file directly: <a href={roseMp4}>{roseMp4}</a>
            </p>
          </div>
        )}
      </div>
    </Layout>
  );
};

export default NewMusic;
