// src/pages/newmusic.tsx
import React, { useState } from "react";
import { Layout, styles } from "../layout/layout";

const NewMusic: React.FC = () => {
  const [videoError, setVideoError] = useState<string | null>(null);
  const mp4Path = "/a-single-pedal-of-a-rose.mp4"; // ensure this file is in /public
  const movPath = "/a%20single%20pedal%20of%20a%20rose.mov"; // optional fallback if you kept the .mov

  const handleVideoError: React.ReactEventHandler<HTMLVideoElement> = (e) => {
    const el = e.currentTarget;
    // Helpful console logging
    console.error("Video failed to load/play.", {
      currentSrc: el.currentSrc,
      networkState: el.networkState, // 3 == NETWORK_NO_SOURCE
      error: el.error,               // May be null on some browsers
    });
    setVideoError("The video could not be loaded.");
  };

  const handleLoadedMetadata: React.ReactEventHandler<HTMLVideoElement> = (e) => {
    const el = e.currentTarget;
    console.log("Video metadata loaded:", {
      duration: el.duration,
      videoWidth: el.videoWidth,
      videoHeight: el.videoHeight,
      currentSrc: el.currentSrc,
    });
    setVideoError(null);
  };

  return (
    <Layout title="New Music">
      <p style={styles.paragraph}>
        This page hosts embedded videos. Below is <em>A Single Pedal of a Rose</em>.
      </p>

      <div style={{ textAlign: "center", margin: "20px 0" }}>
        <video
          controls
          preload="metadata"
          playsInline
          onError={handleVideoError}
          onLoadedMetadata={handleLoadedMetadata}
          style={{
            width: "100%",
            maxWidth: 800,
            borderRadius: 8,
            outline: "none",
            backgroundColor: "#000",
          }}
          // Optional: put a poster image in /public/video-poster.jpg
          // poster="/video-poster.jpg"
        >
          {/* Preferred web format */}
          <source src={mp4Path} type="video/mp4" />
          {/* Optional fallback if your .mov is still around and playable */}
          <source src={movPath} type="video/quicktime" />
          Your browser does not support the video tag.
        </video>

        {videoError && (
          <div style={{ marginTop: 12, color: "#b00020" }}>
            <p style={{ margin: 0 }}>{videoError}</p>
            <p style={{ margin: "6px 0 0" }}>
              Try opening the file directly:
              {" "}
              <a href={mp4Path}>{mp4Path}</a>
            </p>
          </div>
        )}
      </div>
    </Layout>
  );
};

export default NewMusic;
